          <div className="flex-1 min-w-0">
            <p className="text-sm font-medium text-white truncate">
              {user?.name || 'Unknown User'}
            </p>
            <p className="text-xs text-white/70">
              {user?.role ? getRoleDisplay(user.role) : 'User'}
