                    <p className="text-xs text-gray-600 mt-1">"{review.comment}"</p>
                    <p className="text-xs text-gray-500 mt-1">{review.class} â€¢ {review.timeAgo}</p>
                  </div>
                  <Badge variant="secondary" className="ml-2">
                    â˜… {review.rating}
                  </Badge>
                </div>
                {index < recentReviews.length - 1 && <hr className="my-3" />}
              </div>
            ))}
          </CardContent>
        </Card>
      </div>

      {/* Quick Grade Entry */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Star className="h-5 w-5 text-blue-600" />
            <span>Quick Grade Entry - Mathematics Test</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto mb-4">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Student Name</TableHead>
                  <TableHead>Roll No.</TableHead>
                  <TableHead>Marks Obtained</TableHead>
                  <TableHead>Max Marks</TableHead>
                  <TableHead>Grade</TableHead>
                  <TableHead>Status</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {gradeEntryData.map((student, index) => (
                  <TableRow key={index}>
                    <TableCell className="font-medium">{student.name}</TableCell>
                    <TableCell>{student.rollNo}</TableCell>
                    <TableCell>
                      <Input
                        type="number"
                        max={student.maxMarks}
                        defaultValue={student.marks || ''}
                        placeholder="Enter marks"
                        className="w-24"
                      />
                    </TableCell>
                    <TableCell>{student.maxMarks}</TableCell>
                    <TableCell>
                      <Badge variant={student.grade === '-' ? 'secondary' : 'default'}>
                        {student.grade}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      {student.status === 'completed' ? (
                        <span className="text-green-600">âœ“</span>
                      ) : (
                        <span className="text-yellow-600">â³</span>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
          <div className="flex space-x-4">
            <Button className="flex items-center space-x-2">
              <Save className="h-4 w-4" />
              <span>Save Grades</span>
            </Button>
            <Button variant="outline" className="flex items-center space-x-2">
              <Upload className="h-4 w-4" />
              <span>Bulk Upload CSV</span>
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

